INSERT INTO `gen_template_variable_js` VALUES (1815367102035492865, 1814917150020820994, '// 构建表单字段对象的函数\r\nfunction buildFormField(prefix, formField) {\r\n    // 根据前缀和表单字段信息构建表单字段对象\r\n    return {\r\n        id: `${prefix}:${formField.id}`, // 表单字段的唯一标识符\r\n        name: formField.dbFieldName, // 表单字段在数据库中的名称\r\n        type: \"input-text\", // 表单字段的类型\r\n        size: \"full\", // 表单字段的大小\r\n        label: formField.dbFieldTxt, // 表单字段的标签\r\n        hidden: false, // 表单字段是否隐藏\r\n        required: false // 表单字段是否必填\r\n    };\r\n}\r\n\r\n// 替换模板代码中的表单占位符的函数\r\nfunction replaceFormPlaceholders(templateCode) {\r\n    // 遍历 forms 对象的所有键值对\r\n    return Object.entries(forms).reduce((code, [formType, formFields]) => {\r\n        // 使用正则表达式替换模板代码中的表单占位符\r\n        return code.replace(new RegExp(`\"{{${formType}Form}}\"`, \'g\'), `${JSON.stringify(formFields)}`);\r\n    }, templateCode);\r\n}\r\n\r\n// 定义表单字段对象\r\nconst forms = {\r\n    // 生成 View 表单字段\r\n    view: cgformHeadDetails.onlCgformFieldList.map(formField => buildFormField(\'View\', formField)),\r\n    // 生成 Edit 表单字段\r\n    edit: cgformHeadDetails.onlCgformFieldList.map(formField => buildFormField(\'Edit\', formField)),\r\n    // 生成 Insert 表单字段\r\n    insert: cgformHeadDetails.onlCgformFieldList.map(formField => buildFormField(\'Insert\', formField))\r\n};\r\n\r\n// 替换模板代码中的表单占位符\r\nconst updatedTemplateCode = replaceFormPlaceholders(templateCode);\r\nreturn updatedTemplateCode;', '{{forms}}', 1, '2024-07-22 20:43:40', 1, '2024-07-22 20:46:41', 0, NULL);

