DROP TABLE IF EXISTS `sys_oauth_client`;
CREATE TABLE `sys_oauth_client`  (
  `id` bigint NOT NULL COMMENT 'ID',
  `create_by` bigint NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_by` bigint NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `access_token_timeout` bigint NULL DEFAULT NULL COMMENT 'Access-Token 保存的时间（单位：秒）',
  `allow_url` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '应用允许授权的所有URL, 多个用逗号隔开 （可以使用 * 号通配符）',
  `client_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '应用ID',
  `client_secret` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '应用秘钥',
  `client_token_timeout` bigint NULL DEFAULT NULL COMMENT 'Client-Token 保存的时间（单位：秒）',
  `contract_scope` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '应用签约的所有权限, 多个用逗号隔开',
  `is_auto_mode` tinyint(1) NULL DEFAULT 1 COMMENT '是否自动判断此 Client 开放的授权模式',
  `is_client` tinyint(1) NULL DEFAULT 0 COMMENT '是否打开 凭证 模式',
  `is_code` tinyint(1) NULL DEFAULT 0 COMMENT '是否打开 授权码 模式',
  `is_implicit` tinyint(1) NULL DEFAULT 0 COMMENT '是否打开 隐藏式 模式',
  `is_new_refresh` tinyint(1) NULL DEFAULT 0 COMMENT '是否在每次 Refresh-Token 刷新 Access-Token 时，产生一个新的 Refresh-Token',
  `is_password` tinyint(1) NULL DEFAULT 0 COMMENT '是否打开 密码 模式',
  `past_client_token_timeout` bigint NULL DEFAULT NULL COMMENT 'Past-Client-Token 保存的时间（单位：秒）',
  `refresh_token_timeout` bigint NULL DEFAULT NULL COMMENT 'Refresh-Token 保存的时间（单位：秒）',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uidx_client_id`(`client_id` ASC) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '客户端信息' ROW_FORMAT = DYNAMIC;

