  `detail` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL COMMENT '详细的说明，可以是普通文本或 Markdown 格式文本',
  `permission` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '接口权限',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 631 CHARACTER SET = utf8mb3 COLLATE = utf8mb3_general_ci COMMENT = '测试用例文档\r\n后端开发者在测试好后，把选好的测试用例上传，这样就能共享给前端/客户端开发者' ROW_FORMAT = DYNAMIC;

